import React, { Component } from 'react';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // Import the carousel styles
import { Carousel } from 'react-responsive-carousel'; // Import the Carousel component from the library

export class Newsitem extends Component {
  render() {
    let { title, description, imgUrl, NewsUrl, author, date } = this.props;
    return (
      <div className='my-3'>
        <div className="card" style={{ width: "18rem" }}>
          {/* Use the Carousel component for the images */}
          <Carousel showThumbs={false}>
            <div>
              <img src={imgUrl} className="card-img-top" alt="" />
            </div>
            {/* You can add more images if needed */}
          </Carousel>
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}</p>
            <p className="card-text"><small className="text-muted">By {author} on date {new Date(date).toGMTString()}</small></p>
            <a href={NewsUrl} target="_blank" className="btn btn-sm btn-dark">Read More</a>
          </div>
        </div>
      </div>
    );
  }
}

export default Newsitem;
