import React,{useState} from 'react'

export default function Textform(props) {


    const handleLoclick=()=>{

        let newText= text.toLowerCase();
        setText(newText)
        props.showAlert("Converted to LowerCase","success")
            }
    const handleUpclick=()=>{

let newText= text.toUpperCase(); 
setText(newText)
props.showAlert("Converted to UpperCase","success")
    }
    const handleOnChange=(event)=>{
         
        setText(event.target.value);
            } 
    const[text,setText]= useState("");
  return (<>
  <div className="container"style={{color:props.mode==='dark'?'white':'black'}}>
    
<div className="mb-3">
    <h1>{props.heading}</h1>
  <label for="myBox my-3" className="form-label"></label>
  <textarea className="form-control" value= {text} onChange={handleOnChange} id="myBox" rows="8" style={{backgroundColor:props.mode==='dark'?'#0c1229':'white'
,color:props.mode==='dark'?'white':'black'}}></textarea>
</div>
<button disabled={text.length===0} className="btn btn-primary mx-2 my-1" onClick={handleUpclick} style={{color:props.mode==='dark'?'white':'black'}}>Convert To UpperCase</button>
<button disabled={text.length===0} className="btn btn-primary mx-2 my-1" onClick={handleLoclick}style={{color:props.mode==='dark'?'white':'black'}}>Convert To LowerCase</button>
</div>
<div className="container my-2 "style={{color:props.mode==='dark'?'white':'black'}}>
    <h1>Your Text Summary</h1>
    <p>{text.split(" ").filter((element)=>{return element.length!=0}).length}words and {text.length} Charecters
    </p>
    <p>{0.008*text.split(" ").length}Minutes to read</p>
    <h2>Preview</h2>
    <p>{text}</p>
</div>
</> )
}
